mapper.select:
SELECT id,name, gender, natives, birthday, nation, politics, discipline, education, titles, duty, phone, photograph, rests, Personal_profile_id, continue_educationid, 
	education_competitionid, guide_students_teachersid, public_classid, research_testid, teaching_subjectsid FROM teacher 
	
		
			and name like CONCAT ('%',#{name},'%')
		
		
			and gender=#{gender}
		
		
			and politics=#{politics}
		
		
			and education=#{education}

mapper.update:
update teacher
name=#{name}, gender=#{gender}, natives=#{natives}, birthday=#{birthday}, nation=#{nation}, politics=#{politics}, discipline=#{discipline}, education=#{education}, titles=#{titles}, duty=#{duty}, phone=#{phone}, photograph=#{photograph}, rests=#{rests}, Personal_profile_id=#{Personal_profile_id}, title_honorId=#{title_honorId}, continue_educationid=#{continue_educationid}, education_competitionid=#{education_competitionid}, guide_students_teachersid=#{guide_students_teachersid}, public_classid=#{public_classid}, research_testid=#{research_testid}, teaching_subjectsid=#{teaching_subjectsid}, master_growth_planid=#{master_growth_planid}, phased_growth_planningid=#{phased_growth_planningid}, paper_worksid=#{paper_worksid},
where id=#{id}
